mapper.select:
SELECT SUM(sgd.pos_amt) AS pos_amt FROM t_sales_day_total_goods sgd, t_goods gd
sgd.opt_date >= #{startDate} AND sgd.opt_date <= #{endDate} AND sgd.item_subno = gd.subno
AND sgd.org_Id = #{orgId}
AND (sgd.item_barcode = #{itemSubno} OR sgd.item_subno = #{itemSubno})
AND gd.name LIKE #{itemName}
AND gd.item_no IN (${itemType})

SELECT A.*, B.stock_qty FROM ( SELECT sgd.item_subno, SUM(sgd.pos_amt) AS pos_amt, SUM(sgd.pos_qty) AS pos_qty, gd.name AS goods_name FROM t_sales_day_total_goods sgd, t_goods gd
sgd.opt_date >= #{startDate} AND sgd.opt_date <= #{endDate} AND sgd.item_subno = gd.subno
AND sgd.org_Id = #{orgId}
AND (sgd.item_barcode = #{itemSubno} OR sgd.item_subno = #{itemSubno})
AND gd.name LIKE #{itemName}
AND gd.item_no IN (${itemType})
GROUP BY item_subno ) A LEFT JOIN ( SELECT sd.item_subno, SUM(stock_qty) AS stock_qty FROM t_store_detail_one_day sd
sd.org_Id = #{orgId}
GROUP BY item_subno) B ON A.item_subno = B.item_subno ORDER BY pos_amt DESC

SELECT SUM(sgd.pos_qty) AS pos_qty FROM t_sales_day_total_goods sgd, t_goods gd
sgd.opt_date >= #{startDate} AND sgd.opt_date <= #{endDate} AND sgd.item_subno = gd.subno
AND sgd.org_Id = #{orgId}
AND (sgd.item_barcode = #{itemSubno} OR sgd.item_subno = #{itemSubno})
AND gd.name LIKE #{itemName}
AND gd.item_no IN (${itemType})

SELECT A.*, B.stock_qty FROM ( SELECT sgd.item_subno, SUM(sgd.pos_amt) AS pos_amt, SUM(sgd.pos_qty) AS pos_qty, gd.name AS goods_name FROM t_sales_day_total_goods sgd, t_goods gd
sgd.opt_date >= #{startDate} AND sgd.opt_date <= #{endDate} AND sgd.item_subno = gd.subno
AND sgd.org_Id = #{orgId}
AND (sgd.item_barcode = #{itemSubno} OR sgd.item_subno = #{itemSubno})
AND gd.name LIKE #{itemName}
AND gd.item_no IN (${itemType})
GROUP BY item_subno ) A LEFT JOIN ( SELECT sd.item_subno, SUM(stock_qty) AS stock_qty FROM t_store_detail_one_day sd
sd.org_Id = #{orgId}
GROUP BY item_subno) B ON A.item_subno = B.item_subno ORDER BY pos_qty DESC


	SELECT DISTINCT
	    org_id
	FROM
	    t_sales_day_total_goods
	WHERE
	    item_subno = #{itemSubno}
	AND opt_date >= #{startDate}
	AND opt_date <= #{endDate}
