mapper.select:
SELECT sd.org_id, sd.opt_date, g.item_no, SUM(sd.stock_qty) stock_qty, SUM(sd.stock_amt) AS stock_amt FROM t_store_detail sd, t_goods g WHERE g.barcode=sd.item_barcode AND g.item_no IN (${itemType}) AND opt_date = #{optDateEnd}
AND sd.org_id IN (${orgId})
GROUP BY sd.org_id, g.item_no ORDER BY sd.org_id, g.item_no

SELECT sd.org_id, sd.opt_date, g.supplier_bw_id, SUM(sd.stock_qty) stock_qty, SUM(sd.stock_amt) AS stock_amt FROM t_store_detail sd, t_goods g WHERE g.barcode = sd.item_barcode AND g.supplier_bw_id IN (${supplierArray}) AND opt_date = #{optDateEnd}
AND sd.org_id IN (${orgId})
GROUP BY sd.org_id, g.supplier_bw_id ORDER BY sd.org_id, g.supplier_bw_id
